using System;
using FluentAssertions;
using Xunit;

namespace AdventOfCode2017
{
    public class Day1
    {
        public static int Part1(string number)
        {
            int result = 0;

            for (int i = 0; i < number.Length; i++)
            {
                int currentNumber = int.Parse(number[i].ToString());
                int nextNumber = i != number.Length-1 ? int.Parse(number[i+1].ToString()) : int.Parse(number[0].ToString());

                if (currentNumber == nextNumber)
                {
                    result += currentNumber;
                }
            }

            return result;
        }

        public static int Part2(string number)
        {
            int result = 0;

            for (int i = 0; i < number.Length; i++)
            {
                int currentNumber = int.Parse(number[i].ToString());
                int halfwayAroundIndex = (i + (number.Length / 2)) % number.Length;
                int numberHalfwayAround = int.Parse(number[halfwayAroundIndex].ToString());

                if (currentNumber == numberHalfwayAround)
                {
                    result += currentNumber;
                }
            }

            return result;
        }
    }

    public class Day1Part1Tests
    {
        [Fact]
        public void Test1_1122_yields_3()
        {
            Day1.Part1("1122").Should().Be(3);
        }

        [Fact]
        public void Test2_1111_yields_4()
        {
            Day1.Part1("1111").Should().Be(4);
        }

        [Fact]
        public void Test3_1234_yields_0()
        {
            Day1.Part1("1234").Should().Be(0);
        }

        [Fact]
        public void Test4_91212129_yields_9()
        {
            Day1.Part1("91212129").Should().Be(9);
        }

        [Fact]
        public void Day1_part1_solution()
        {
            var result = Day1.Part1("");

            result.Should().Be(1119);
        }
    }

    public class Day1Part2Tests
    {
        [Fact]
        public void Test1_1212_yields_6()
        {
            Day1.Part2("1212").Should().Be(6);
        }

        [Fact]
        public void Test2_1221_yields_0()
        {
            Day1.Part2("1221").Should().Be(0);
        }

        [Fact]
        public void Test3_123425_yields_4()
        {
            Day1.Part2("123425").Should().Be(4);
        }

        [Fact]
        public void Test4_123123_yields_12()
        {
            Day1.Part2("123123").Should().Be(12);
        }

        [Fact]
        public void Test4_12131415_yields_4()
        {
            Day1.Part2("12131415").Should().Be(4);
        }

        [Fact]
        public void Day1_part2_solution()
        {
            var result = Day1.Part2("");

            result.Should().Be(1420);
        }
    }
}